import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Import useHistory from react-router-dom
import Challenges from './main';

const Quizzpopup = () => {
    const [Showpopsubs, setShowpopSubs] = useState(false);
    const history = useHistory(); // Initialize useHistory


    const ContinueHandled = () => {
        console.log("Showpopsubs", )
      //  setShowpopSubs (!Showpopsubs)
        history.push("/challenges");

    }
    return(
        <>
            <div className="Popup">
                <h2>Quizz Guide</h2>
                <span className="info">1  Lorem, ipsum dolor sit amet consectetur </span>
                <span className="info">2  Lorem, ipsum dolor sit amet consectetur </span>
                <span className="info">3  Lorem, ipsum dolor sit amet consectetur </span>
                <span className="info">4  Lorem, ipsum dolor sit amet consectetur </span>
                <span className="info">5  Lorem, ipsum dolor sit amet consectetur </span>
                <div className="info-btn">
                    <button className="exit-btn">Exit Quizz</button>
                    <a href="#" className="cont-btn" onClick={ContinueHandled}>Continue</a>
                </div>
            </div>
            {Showpopsubs && <Challenges />}
        </>
    )
}

export default Quizzpopup;
