import React from "react";
// import Q from "./Quizz";
const QuizzUI = (props) => {
    const [question , incorrect_answers , correct_answer] = props.QuizData;
    const options = [correct_answer, ...incorrect_answers];


    const SubmitHandled = ()=>{
        alert ("submitted")
    }

    return(
        <>
        <div className="card ">
            <div className="row">
                <div className="col">
                    <h3>{question}</h3>
                    <hr />
                </div>
                <div className="col">
                    <ul>
                    {/* <li>{props.incorrect_answers} 1</li>
                    <li>{props.incorrect_answers} 2</li>
                    <li>{props.correct_answer} 3</li>
                    <li>{props.incorrect_answers} 4</li> */}
                    {options.map((option , index)=>{
                        <li key={index}>{option}</li>
                    })}
                    </ul>
                </div>
                <div className="submit">
                    <button type="submit" onClick={SubmitHandled} >Submit</button>
                </div>
            </div>
        </div>


        </>
    )
}

export default QuizzUI;
