import React, { useState } from "react";
import QuizData from "./Quizz";

const QuizUI = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const handleOptionClick = (selectedAnswer) => {
    // Check if selectedAnswer is correct
    if (selectedAnswer === QuizData[currentQuestion].correct_answer) {
      setScore(score + 1);
    }

    // Move to the next question
    if (currentQuestion < QuizData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setScore(0);
  };

  return (
    <div className="container">
      {currentQuestion < QuizData.length ? (
        <div className="card ">
          <div className="row">
            <h3 className="col">
              {QuizData[currentQuestion].question}</h3>
            <hr />
            <ul>
              {QuizData[currentQuestion].incorrect_answers.map(
                (option, index) => (
                  <li key={index}>
                    <button
                      className="btn btn-primary"
                      onClick={() => handleOptionClick(option)}
                      disabled={score > 0}
                    >
                      {option}
                    </button>
                  </li>
                )
              )}
              <li>
                <button
                  className="btn btn-primary"
                  onClick={() =>
                    handleOptionClick(QuizData[currentQuestion].correct_answer)
                  }
                  disabled={score > 0}
                >
                  {QuizData[currentQuestion].correct_answer}
                </button>
              </li>
            </ul>
          </div>
        </div>
      ) : (
        <div className="mt-5">
          <h3>Congratulations!</h3>
          <p>
            Your final score: {score}/{QuizData.length}
          </p>
          <button className="btn btn-primary" onClick={handleRestart}>
            Try Again
          </button>
        </div>
      )}
    </div>
  );
};

export default QuizUI;
