import "bootstrap/dist/css/bootstrap.min.css";
import React, { useRef, useState } from 'react';
import { data } from '../Assets/data';
import '../Components/Quizz.css';
const Challenges = () => {
    let [index, setIndex] = useState(0);
        let [question , setQuestion] = useState(data[index]);
        let [lock , setLock] = useState(false);
        let [score, setScore] = useState(0);
        let options1 = useRef(null);
        let options2 = useRef(null);
        let options3 = useRef(null);
        let options4 = useRef(null);

        let optionArray = [options1, options2, options3, options4];
        console.log(optionArray);

        let [result, setResult] = useState(false);

        const checkAnswer =(el,ans) => {
            if(lock === false){
            if(question.ans === ans){
                el?.target?.classList?.add("correct");
                setLock(true);
                optionArray =[question.ans-1]?.current?.classList?.add("correct")
                setScore(score + 1);
            }else{
                el?.target?.classList?.add("Wrong");
                setLock(true);
                optionArray =[question.ans-1]?.current?.classList?.add("Wrong")
            }
        }

    }

    const reset = () => {
        setIndex(0);
        setQuestion(data[0]);
        setScore(0);
        setLock(false);
        setResult(false);
    }

    const next = () => {

        if(lock === true){
            if(index === data.length -1){
                setResult(true);
                return 0;
            }
            setIndex (++index);
            setQuestion(data[index]);
            setLock(false);
            optionArray.map((options)=>{
                options.current.classList.remove("Wrong");
                options.current.classList.remove("correct");
                return null;
            })
        }
    }
    console.log(next)
    return (
        <>

            <div className="heading">
                <h1>CHALLENGES YOURSELF WITH <br /> OUR QUIZZES!</h1>
                <p>Test Your Knowledges and Learn New Things!</p>
            </div>
            <div className="Main-img-sub"> 
            <div className="Images">
                <h3>BRAINYQUIZ - THE <br/>SMARTER WAY TO PLAY !</h3>
                <img src="./images/brainyquizz.jpg" alt="Challenges laptop" />
                <div className="list">
                    <h4>Related Quizz</h4><br/>
                    <h5>Go throug</h5>
                    <ul>
                        <li >1  Dashboard</li>
                        <li >2  Subject</li>
                        <li >3  React JS</li>
                        <li >4  HTML</li>
                        <li >5  CSS</li>
                        <li >6  Javascript</li>
                        <li >7  Tailwind</li>
                        <li >8  BootStrap</li>
                        <li >9  NodeJS </li>
                    </ul>
                    <div class="dropdown">

                    <div class="input-group mb-3">
  <div class="input-group-prepend">
    <label class="input-group-text" for="inputGroupSelect01">Options</label>
  </div>
  <select class="custom-select" id="inputGroupSelect01">
    <option selected>Choose...</option>
    <option value="1">One</option>
    <option value="2">Two</option>
    <option value="3">Three</option>
  </select>
</div>

<div class="input-group mb-3">
  <select class="custom-select" id="inputGroupSelect02">
    <option selected>Choose...</option>
    <option value="1">One</option>
    <option value="2">Two</option>
    <option value="3">Three</option>
  </select>
  <div class="input-group-append">
    <label class="input-group-text" for="inputGroupSelect02">Options</label>
  </div>
</div>

<div class="input-group mb-3">
  <div class="input-group-prepend">
    <button class="btn btn-outline-secondary" type="button">Button</button>
  </div>
  <select class="custom-select" id="inputGroupSelect03">
    <option selected>Choose...</option>
    <option value="1">One</option>
    <option value="2">Two</option>
    <option value="3">Three</option>
  </select>
</div>




                </div>
            </div>

            <div className="Subscribe">
                <h1>Join Us, Subscribe to Our BRAINYQUIZ  </h1>
            <div className='container'>
        <div className='border'>
            <div className='gap'>
        <h1>Quizz UI</h1>

        <hr/>
        {result? <></>:<><h3>{index+1}. {question.question}</h3>
        <ul>
            <li ref={options1} onClick={(el)=>{checkAnswer(el,1)}}>{question.options1}</li>
            <li ref={options2} onClick={(el)=>{checkAnswer(el,2)}}>{question.options2}</li>
            <li ref={options3} onClick={(el)=>{checkAnswer(el,3)}}>{question.options3}</li>
            <li ref={options4} onClick={(el)=>{checkAnswer(el,4)}}>{question.options4}</li>
        </ul>
        <div className="center">
        <button className='btn btn-danger text-center  ml-4 justify-center items-center' onClick={next}>Next</button>
        <div className='index'>{index+1} of {data.length} questions</div>
        </div>

        </>}
        {result?<>
            <div className="center text-bold" style={{color:"white"}}>
                <p>Your Scored {score} out of {data.length} </p>
        <button className="btn btn-info p-2" onClick={reset}>Reset</button>
        </div>
        </>:<></>}



        </div>
        </div>
        </div>
            </div>
            </div>
            </div>
        </>
    )
}

export default Challenges;
