import QuizzPopup from "./Quizzpopup";


const header = () => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
        const [showPopup , setShowPopup] = useState(false);


        const handleStartQuiz = () => {
            console.log('showPopup',showPopup)
            setShowPopup(!showPopup)
}

const backgroundImageStyle = {
    backgroundImage: "url(../public/images/QuizLogoRemove.png)",
    backgroundSize: "cover", // or 'contain', 'auto', specific width/height values
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    width: "100%", // Set width
    height: "90vh", // Set height
  };

    return(
        <>
        <div id="header" style={backgroundImageStyle}>
            <img src="./public/images/QuizLogoRemove.png" alt="logo" style={{width:"150px" , height:"250px"}} />

            <div className="Quiz-left col col-lg-4 col-md-6 col-sm-12">
                <h1>DynamicTechQuizzes</h1>
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Est amet debitis, ad labore minima alias laudantium tempora quos necessitatibus possimus deleniti voluptatibus cupiditate laboriosam, cumque ab incidunt rem assumenda error!</p>
                <button className="btn"  id="bttn" onClick={handleStartQuiz}>Start Quiz</button>
            </div>


        </div>
        {showPopup && <QuizzPopup />}




        </>
    )
}
export default header;
