import React , {useState} from "react";




const loginForm = () => {

    const [fullName , setFullName] = ({
        email:"",
        password:""
    })

    const InputEvent = (event) =>{
        const value = event.target.value;
        const name = event.target.name;

        setFullName((preValued)=>{

                if(name === 'email'){
                    return{
                        email : value,
                        password : preValued.password
                    }
                }
                    else if(name === "password"){
                        return{
                            email: preValued.email,
                            password : value
                        }
                    }
        })
    }

    const SubmitEvent = (event) =>{
        event.preventDefault();
        alert("form Submiitted")
    }

    return(
        <>
            <div className="PopupLogin mx-5 my-5 m-2">
            <div className="card text-center align-center justify-center p-4" style={{width:"100%", height:"50vh", backgroundColor:"transparent" }}>
            <div className="container-2">
                {/* <img src="./latest.jpg" alt="image" width="100px" height="50px"> */}
            <div className="form-2">
                <form onSubmit={SubmitEvent} >
                <h1>Sign up</h1>
                <p>Enter your email and password</p>
                <input 
                type="email" 
                name="email" 
                placeholder="enter your email" 
                onChange={InputEvent}
                value={fullName.email}
                />
                <input 
                type="password"
                name="password" 
                placeholder="enter your password" 
                onchange={InputEvent}
                value={fullName.password}
                />

                <button type="submit">submit</button>
                <a>Need help</a>
                <a>Sign up</a>

                </form>
            </div>
                </div>    
        </div>
        </div>
        </>
    )
}


export default loginForm;
