import React, { useState } from "react";
import { Link } from "react-router-dom";
import LoginForm from "../Components/loginForm";
import "./navbar.css";

const Navbar = () => {
  const [showLogin, setShowLogin] = useState(false);

  const loginHandled = () => {
    setShowLogin(!showLogin);
  };

  const myStyle = {
    backgroundColor: "lightyellow",
    padding: "10px",
    borderRadius: "5px",

    height: "8vh",
  };
  return (
    <>
      <nav>
        <div id="logo">
          <img src="./images/BlackLogo.png" alt="logo.png" style={myStyle} />
        </div>
        <ul className="navLi">
          <Link className="li" to="/">Home </Link>
          <Link className="li" to="/about">About</Link>
          <Link className="li" to="/Service">Services</Link>
          <Link className="li" to="/contact">Contact</Link>
        </ul>

        <button className="btn loginButon" type="submit" onClick={loginHandled}>
          Login
        </button>
      </nav>

      {showLogin && <LoginForm />}
    </>
  );
};

export default Navbar;
