import React, { useState } from "react";
import LoginForm from "../Components/loginForm";
import {Link} from 'react-router-dom'
import './navbar.css';

const  Navbar = () => {

    const [showLogin , setShowLogin] = useState(false);

    const loginHandled = () => {
        setShowLogin(!showLogin)
    }


    const myStyle = {
        backgroundColor: 'lightyellow',
        padding: '10px',
        borderRadius: '5px',

        height:"8vh"

    };
    return(
        <>
        <nav>
            <div id="logo">
                <img src="./images/logo.png" alt="logo.png" style={myStyle} />
            </div>
    <ul className="navLi" >
        <Link>Home </Link>
        <Link>About</Link>
        <Link>Services</Link>
        <Link>Contact</Link>

    </ul>

    <button className="btn loginButon" type="submit"  onClick={loginHandled}>Login</button>

    </nav>


    {showLogin && <LoginForm/>}
    </>
);

}

export default Navbar;
